<?php











namespace Composer;

use Composer\Semver\VersionParser;






class InstalledVersions
{
private static $installed = array (
  'root' => 
  array (
    'pretty_version' => '1.0.0+no-version-set',
    'version' => '1.0.0.0',
    'aliases' => 
    array (
    ),
    'reference' => NULL,
    'name' => 'samir_hussein/mvc_fromework',
  ),
  'versions' => 
  array (
    'mervick/emojionearea' => 
    array (
      'pretty_version' => 'v3.4.x-dev',
      'version' => '3.4.9999999.9999999-dev',
      'aliases' => 
      array (
      ),
      'reference' => 'bd5bb46d64683ad9d8a6e9753be53e6f4e6bee8a',
    ),
    'samir_hussein/mvc_fromework' => 
    array (
      'pretty_version' => '1.0.0+no-version-set',
      'version' => '1.0.0.0',
      'aliases' => 
      array (
